import React, { createContext, useEffect, useState } from 'react'
import axios from 'axios';//Axios é um cliente HTTP baseado em Promises para fazer requisições.
import keys from './config/keys'

const GlobalContext = createContext({
    name:"default",
    cargo:'default'
});

export const GlobalContextProvider = ({ children }) => {
    const [userObject, setUserObject] = useState({
        name:"default",
        cargo:'default'
    });
    const [cargo, setCargo]=useState();

  useEffect(() => {
    console.log(keys.serverApi)
    axios.get('http://'+keys.serverApi + "/getuser", { withCredentials: true }).then((res) => {
        console.log(res.data);
        if (res.data) {
            setUserObject(res.data);
        }
    })
  }, []);

  return (
    <GlobalContext.Provider
      value={{ name: userObject.name , cargo: userObject.cargo }}>
      {children}
    </GlobalContext.Provider>
  );
};

export function useGlobalContext() {
  const context = useContext(GlobalContext);

  if (!context) {
    throw new Error("useGlobalContext must be used within an GlobalContextProvider.");
  }

  return context;
}

export default GlobalContext;
